border:2px solid black;
padding:5px;
font-size:1.4em
}

.ipa {
font-size:0.7em;
display:block;
color:blue;
padding:0 0 5px 0px;
}

.container {
border:0px solid;
display:table;
margin:auto;
}

.or {
display:table-cell;
vertical-align:middle;
padding:0 10px
}
.translation {
font-size:0.6em;
display:block;
color:gray;
}

